import { FaRegBell } from "react-icons/fa";
import { CiShop } from "react-icons/ci";
import { AiOutlineShopping, AiOutlineHeart } from "react-icons/ai";
import {
  Box,
  HStack,
  Divider,
  Stack,
  useDisclosure,
  Image,
  Menu,
  MenuButton,
  Avatar,
  MenuList,
  MenuItem,
  useToast,
  ToastId,
  Flex,
} from "@chakra-ui/react";
import { Link, useNavigate } from "react-router-dom";
import LoginModal from "./LoginModal";
import SearchBar from "./SearchBar"; // Import the SearchBar component
import { Text } from "@chakra-ui/react";
import SignUpModal from "../index/SignUpModal";
import useUser from "../../lib/useUser";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useRef } from "react";
import { logOut } from "../../api";

export default function Header() {
  const { userLoading, isLoggedIn, user } = useUser();
  const {
    isOpen: isLoginOpen,
    onClose: onLoginClose,
    onOpen: onLoginOpen,
  } = useDisclosure();
  const {
    isOpen: isSignUpOpen,
    onClose: onSignUpClose,
    onOpen: onSignUpOpen,
  } = useDisclosure();

  const queryClient = useQueryClient();
  const navigate = useNavigate();

  const mutation = useMutation(logOut, {
    onMutate: () => {},
    onSuccess: () => {
      queryClient.refetchQueries(["me"]);
      navigate("/");
    },
  });
  const onLogOut = async () => {
    mutation.mutate();
  };
  console.log(user);

  const handleSearchClick = (keyword) => {
    if (keyword.trim() !== "") {
      const newUrl = `/items/${encodeURIComponent(
        "모든작품"
      )}?&search=${keyword}`;
      window.location.href = newUrl;
    }
  };
  return (
    <Box width={"1280px"} height={"90px"}>
      <Stack
        alignSelf={"stretch"}
        justifyContent={"space-between"}
        alignItems="center"
        direction={{
          // sm: "column",
          md: "row",
        }}
        spacing={{
          sm: 4,
          md: 0,
        }}
        height={90}
      >
        <Box mr={0} ml={"0px"} color={"black"}>
          <Link to={"/"}>
            {" "}
            <SvgArtantLogo />
          </Link>
        </Box>

        <SearchBar
          placeholder={"search for anything"}
          width="700px"
          height={"48px"}
          type={true}
          onSearch={handleSearchClick}
        />

        <HStack spacing={2} alignItems={"center"}>
          {!userLoading ? (
            !isLoggedIn ? (
              <>
                <Box
                  as="button"
                  onClick={onLoginOpen}
                  bg="transparent"
                  border="none"
                  _hover={{ bg: "transparent" }}
                  _active={{ bg: "transparent" }}
                  _focus={{ outline: "none" }}
                  height={"16px"}
                >
                  <Text fontSize="13px">로그인</Text>
                </Box>
                <Text>•</Text>
                <Box
                  as="button"
                  onClick={onLoginOpen}
                  bg="transparent"
                  border="none"
                  _hover={{ bg: "transparent" }}
                  _active={{ bg: "transparent" }}
                  _focus={{ outline: "none" }}
                  height={"16px"}
                >
                  <Text fontSize="13px">회원가입</Text>
                </Box>
                <Text>•</Text>
                <Box color={"black"} height={"16px"}>
                  <Link to={"/"}>
                    <Text fontSize="13px">고객센터</Text>
                  </Link>
                </Box>
                <Divider
                  orientation="vertical"
                  height="16px"
                  borderColor="#D9D9D9"
                  mx={2}
                />{" "}
                <Box color={"#1C1B1F"} height={"24px"}>
                  <Link to={"/"}>
                    <SvgHeart />
                  </Link>
                </Box>
                <Box color={"#1C1B1F"} height={"24px"}>
                  <Link to={"/"}>
                    <SvgBasket />
                  </Link>
                </Box>
              </>
            ) : (
              <Flex alignItems={"center"} gap={"5px"}>
                <Text as={"button"} onClick={onLogOut} fontSize="13px">
                  로그아웃
                </Text>
                <Text>•</Text>
                <Link to={"/"}>
                  <Text fontSize="13px">고객센터</Text>
                </Link>
                <Divider
                  orientation="vertical"
                  height="20px"
                  borderColor="#D9D9D9"
                  mx={2}
                />{" "}
                <Box color={"#1C1B1F"} height={"24px"}>
                  <Link to={`/people/${user?.pk}`}>
                    <SvgHeart />
                  </Link>
                </Box>
                <Box color={"#1C1B1F"} height={"24px"}>
                  <Link to={"/"}>
                    <SvgBell />
                  </Link>
                </Box>
                <Menu>
                  <MenuButton>
                    <Box color={"#1C1B1F"} height={"24px"}>
                      <Link to={`people/${user?.pk}`}>
                        <Avatar
                          name={user?.name}
                          src={user?.avatar}
                          size="xs"
                        />
                      </Link>
                    </Box>
                  </MenuButton>
                  <MenuList>
                    {/* {user?.is_host ? (
                      <Link to="/rooms/upload">
                        <MenuItem>Upload room</MenuItem>
                      </Link>
                    ) : null} */}
                    <Link to={`people/${user?.pk}`}>
                      <MenuItem>
                        <Flex
                          padding="6px 18px"
                          gap="12px"
                          alignItems={"center"}
                        >
                          <Avatar
                            src={user?.avatar}
                            width={"24px"}
                            height={"24px"}
                          />
                          <Flex flexDirection={"column"}>
                            <Text fontSize={"16px"} fontWeight={"500"}>
                              이름
                            </Text>
                            <Text>프로필 보기</Text>
                          </Flex>
                        </Flex>
                      </MenuItem>
                    </Link>
                    <MenuItem>구매내역 및 리뷰</MenuItem>
                    <MenuItem>메시지</MenuItem>
                    <MenuItem>특별 제공</MenuItem>
                    <Link to="/your/account">
                      <MenuItem>계정 설정</MenuItem>
                    </Link>
                    <Link to="/your/shops/register">
                      <MenuItem>상점 개설</MenuItem>
                    </Link>
                    <MenuItem onClick={onLogOut}>로그아웃</MenuItem>
                  </MenuList>
                </Menu>
                <Box color={"#1C1B1F"} height={"24px"}>
                  <Link to={"/your/shops/me/"}>
                    <SvgShop />
                  </Link>
                </Box>
                <Box color={"#1C1B1F"} height={"24px"}>
                  <Link to={"/cart"}>
                    <SvgBasket />
                  </Link>
                </Box>
              </Flex>
            )
          ) : null}
        </HStack>

        <LoginModal isOpen={isLoginOpen} onClose={onLoginClose} />
        <SignUpModal isOpen={isSignUpOpen} onClose={onSignUpClose} />
      </Stack>
    </Box>
  );
}

